import styled from 'styled-components';

import {rgb, shade} from 'polished';

export const NavHeader  = styled.div `
position:absolute;
z-index:999;
top:0;
left:0;
right:0;
height:65px;
background:#fff;

`

export const NavLogo = styled.div`
background-color:#fff;
align-items:center;
display:flex;
margin-right: 5px;

.logo {
    right:0
}
.logo img { 
    margin-top:10px;
    margin-left:10px;
    margin-bottom:10px;
    height:45px;
    display:inline-block;
    vertical-align:middle
}
`

export const Title = styled.h1`
font-size:48px;
color:#3A3A3A;
max-width:45px;
line-height:56px;
margin-top:80px;
margin-left:6rem;
`


export const Form = styled.form`
margin-top:2.5rem;
max-width:700px;
display:flex;
margin-left:6rem;


input {
    flex: 1;
    height: 70px;
    padding: 0 24px;
    border: 0;
    border-radius:5px 0 0 5px;
    color:#3a3a3a;
    border:2px solid;
    background-color: rgb(236,240,241);


    &::placeholder{
        color:#a8a8b3;
    }
}

button {
    width:210px;
    height:70px;
    background:rgb(36,41,46);
    border-radius:0px 5px 5px 0px;
    border:0;
    color:#fff;
    font-weight:blanchedalmond;
    transition:background-color 0.2s;

    &:hover {
        background: ${shade(0.2,rgb(36,41,46))};
     }
}
`
