import React,{useState,useEffect} from 'react';
import {ReposStyles, Section} from './styles';



const Repos = ({repoData}:any)=> {
    const [topRepos,setTopRepos] = useState([]);
    const [sortType,setSortType] = useState('stars');
    const [dropdownOpen,setDropdownOpen] = useState(false)





    const map = {
        stars:'stargazers_count',
        forks :'forks_count',
        size:'size',
    };

const sortTypes = ['stargazers_count','forks_count','size']


type typed = "stars"|"forks"|"size"

const getTopRepos = (typed:string) => {



    const LIMIT = 8;



    const sorted = repoData.filter((repo: { fork: any; }) => !repo.fork).sort((a: { [x: string]: number; },b: { [x: string]: number; }) => b[typed] - a[typed]).slice(0,LIMIT);
      setTopRepos(sorted)

}

useEffect(() => {
    if (repoData) {
      getTopRepos(repoData);
    }
  }, []);

const toggleDropdown = () => setDropdownOpen(!dropdownOpen);

const changeRepoSort = (sortType:string) => {
    setSortType(sortType);
    toggleDropdown();
}


  return (
      <Section>
          <ReposStyles>
          <h2>Top Repos</h2>
          <div className = "dropdown-wrapper">
              <span className = "label">by</span>
              <button className= "dropdown_button" onClick = {()=>toggleDropdown()}>
                  <label>{sortType}</label>
              </button>
              <ul className="dropdown_list">
                  {sortTypes.map((type,i)=> (
                      <li className="dropdown_list-item" key={i}>
                          <button onClick = {() => changeRepoSort(type)}>{type}</button>
                      </li>
                  ))}
              </ul>

          </div>


          </ReposStyles>
      </Section>



  )

}

export default Repos;
