import react, {useState, useEffect } from 'react';
import {useRouteMatch} from 'react-router-dom';
import GhPolyglot from 'gh-polyglot';
import {Section,UserInfoStyles,Graphs,BgColor, Calendar} from './styles';
import Charts from '../../Components/Charts';
import {FiCalendar } from 'react-icons/fi';
import { GoLocation } from 'react-icons/go';
import Repos from '../../Components/Repos'
import React from 'react';
import Header from '../../Components/Header/index'

import GitHubCalendar from 'react-github-calendar';
import ReactTooltip from 'react-tooltip';

import Lottie from "react-lottie";
import animationData from '../../lotties/githubFile.json';




interface UserParams  {
    user:string;

}

interface userData {
avatar_url:string;
name:string;
created_at:string;
location:string;
login:string;
html_url:string;
public_repos:string;
followers:string;
following:string;
}


const UserData:React.FC= ()  => {

    const [userData,setUserData] = useState<userData|null>(null);

    const[langData,setLangData] = useState(null);
    const[repoData,setRepoData] = useState(null);
    const [spinner,setSpinner] = useState(true);
    const [loading,setLoading] = useState(false);
    const [completed,setCompleted] = useState(false);


    const [error,setError] = useState({active:false,type:200})

    const userGeneral = useRouteMatch<UserParams>()
    const username = userGeneral.params.user

    const defaultOptions1 = {
      loop: true,
      autoplay: true,
      animationData: animationGit.default,
      rendererSettings: {
        preserveAspectRatio: "xMidYMid slice",
      },
    };




    const getUserData = () => {

        fetch(`https://api.github.com/users/${username}`)
        .then(response => {
            if (response.status === 404) {
                return setError ({active: true, type:404})
            }
            if(response.status===403) {
                return setError({active:true,type:403});
            }

            return response.json();



            }).then(json => setUserData(json))
            .catch(error=> {
                setError({active:true,type:400});
                console.error ('Error:',error);
            })
        };


      const getLangData = () => {
          const me = new GhPolyglot(`${username}`);

          me.userStats((err: Error, stats: react.SetStateAction<null>) => {
            if (err) {
              console.error('Error:', err);
              setError({ active: true, type: 400 });
            }

            setLangData(stats);


          }); 

        };



  const getRepoData = () => {
    fetch(`https://api.github.com/users/${username}/repos?per_page=100`)
      .then(response => {
        if (response.status === 404) {
          return setError({ active: true, type: 404 });
        }
        if (response.status === 403) {
          return setError({ active: true, type: 403 });
        }

        return response.json();

      })
      .then(json => setRepoData(json))
      .catch(error => {
        setError({ active: true, type: 200 });
        console.error('Error:', error);
      });

  };

  useEffect(()=> {
    setTimeout(()=> {
    getRepoData();
    getLangData();
    getUserData();

    setTimeout( () => {
      setCompleted(true);
    }, 1000);
  },1000)
  },[])

useEffect(()=> {
  setTimeout(()=>setSpinner(false),1000)


},[])

  return (
    !completed? (
        <>
        <Lottie options={defaultOptions1} height={200} width={200} />

        </>
        ):(
    <>
    <Header/>
   {userData && (
     <>
     <BgColor>
     <Section>
            <UserInfoStyles>
                {userData.avatar_url && (
                  <div className ="avatar">
                    <img src = {userData.avatar_url} alt ="avatar"/>
                  </div>
                )}
      {userData.name && <h1>{userData.name}</h1>}

      {userData.login && (
          <h2>
            <a href={userData.html_url} target="_blank" rel="noopener noreferrer">
              @{userData.login}
            </a>
          </h2>
        )}  
<div className= "info">
{userData.location && (
            <span className="info__item">
              <GoLocation className ="icon" size="16" />
              {userData.location}
            </span>
          )}

      {userData.created_at && (
        <>
        <span className ="info__item">
          <FiCalendar className= "icon" size = "16"/>
          Joined{' '}
          {new Date (userData.created_at).toLocaleDateString('pt-BR',{
            month: 'long',
            day: 'numeric',
            year: 'numeric'  
                   })}
        </span>
      </>
      )}

</div>
       <div className="stats">
          <div className="stats__item">
            <span className="num">{userData.public_repos.toLocaleString()}</span>
            <span className="num-label">Repositories</span>
          </div>
          <div className="stats__item">
            <span className="num">{userData.followers.toLocaleString()}</span>
            <span className="num-label">Followers</span>
          </div>
          <div className="stats__item">
            <span className="num">{userData.following.toLocaleString()}</span>
            <span className="num-label">Following</span>
          </div>
        </div>

      </UserInfoStyles>
      </Section>
      <Graphs>
        <div className = "chart">

          <div className = "chart-container">
          {langData && repoData && <Charts id = "langChart" langData={langData} repoData={repoData}/>}


          </div>
        </div>

        </Graphs>
        <Calendar>
        <GitHubCalendar year={2021} blockMargin={13} username={username}>
        <ReactTooltip html />
        </GitHubCalendar>
        </Calendar>
        <Repos repoData={repoData}/>

        </BgColor>
            </>

        )
                }


 </>
  )
  )

};
export default UserData
