import { rgb } from "polished";

const theme = {
    colors: {
      orange: '#f66a0a',
      green: '#28a745',
      blue: '#0070f3',
      lightblue: '#79b8ff',
      lightestBlue: '#c8e1ff',
      offWhite: '#f6f8fa',


      grey: '#6a737d',
      grey2: '#586069',

      gray100:'#EDF2F7',
      gray200:'#E2E8F0',
      gray300:'#CBD5E0',
      gray800:'#1A202C',
      gray500:'#718096',
      gray700:'#2D3748',

      black: '#1A1E22',
      darkGrey: '#24292e',
      moreDarkGrey: rgb(263,30,34),
      white: '#FFFFFF',
    },

    fonts: {
      inter: 'Inter, system, -apple-system, BlinkMacSystemFont, Roboto, Segoe UI, Arial, sans-serif',
      mono: 'SF Mono, Fira Code, Fira Mono, Roboto Mono, Lucida Console, Monaco, monospace',
    },

    transition: `all 0.2s ease-in-out`,
  };

  export default theme;
