import styled,{ css } from 'styled-components';

import {rgb, shade} from 'polished';

// padding : 40 20

export const Title = styled.h1`
font-size:48px;
color:#3A3A3A;
max-width:45px;
line-height:56px;
margin-top:80px;
`


export const Form = styled.form`
margin-top:2.5rem;
max-width:700px;
display:flex;
margin-left:6rem;


input {
    flex: 1;
    height: 70px;
    padding: 0 24px;
    border: 0;
    border-radius:5px 0 0 5px;
    color:#3a3a3a;
    border:2px solid;
    background-color: rgb(236,240,241);


    &::placeholder{
        color:#a8a8b3;
    }
}

button {
    width:210px;
    height:70px;
    background:rgb(36,41,46);
    border-radius:0px 5px 5px 0px;
    border:0;
    color:#fff;
    font-weight:blanchedalmond;
    transition:background-color 0.2s;

    &:hover {
        background: ${shade(0.2,rgb(36,41,46))};
     }
}
`

export const Users = styled.div `
margin-top:80px;
max-width:700px;

 a {
     background : #E5E7E9;
     border-radius: 5px;
     width:100%;
     padding:24px;
     display:block;
     text-decoration:none;

     display:flex;
     align-items:center;
     transition: transform 0.2s;

& + a {
    margin-top: 16px;

}

&:hover {
    transform:translateX(10px);
}

 img {
     width: 64px;
     height: 64px;
     border-radius:50%
 }

 div {
     margin: 0 16px;
     flex:1;

 }
 }

`
