import styled from 'styled-components';
import {theme,mixins,media} from '../../styles';
const {colors, fonts} = theme;


export const ReposStyles  = styled.div`

.dropdown-wrapper {
    display:flex;
    align-itens:center;
    font-size: 1rem;
    color: ${colors.grey};


    .label { 
        margin: 0 1rem;
    }
}

.repo-list {
    u1 {
        display:grid;
        grid-template-columns: repeat(auto-fit,minmax(300px,1fr));
        grid-gap : 1rem;
    }


}
`
